INSERT INTO `zz_plugins` (`id`, `name`, `title`, `idmodule_from`, `idmodule_to`, `position`, `script`, `enabled`, `default`, `order`, `compatibility`, `version`, `options2`, `options`, `directory`, `help`, `created_at`, `updated_at`) VALUES
(54,	'Articoli da evadere',	'Articoli da evadere',	24,	24,	'tab_main',	'',	1,	1,	0,	'2.*',	'0.1',	'',	'{\"main_query\": [ { \"type\": \"table\", \"fields\": \"Cod. articolo, Descrizione, Num. ordine, Q.ta ordinata, Magazzino di partenza, Q.ta evasa, Q.ta da evadere, Cliente\", \"query\": \"SELECT or_ordini.idstatoordine,\r\n(SELECT `id` FROM `zz_modules` WHERE `name` = \'Ordini cliente\') AS _link_module_,\r\nor_ordini.id as _link_record_,\r\nor_righe_ordini.idarticolo as \'Cod. articolo\', \r\nmg_articoli.descrizione as \'Descrizione\', \r\nor_ordini.numero as \'Num. ordine\', \r\nor_righe_ordini.qta as \'Q.ta ordinata\',\r\nan_sedi.nomesede as \'Magazzino di partenza\',\r\nor_righe_ordini.qta_evasa as \'Q.ta evasa\',\r\n(or_righe_ordini.qta - or_righe_ordini.qta_evasa) AS \'Q.ta da evadere\', \r\nconcat(an_anagrafiche.codice, \' - \', an_anagrafiche.ragione_sociale) as \'Cliente\'\r\nFROM or_righe_ordini \r\nINNER JOIN or_ordini ON or_ordini.id = or_righe_ordini.idordine \r\nINNER JOIN mg_articoli ON mg_articoli.id = or_righe_ordini.idarticolo \r\nINNER JOIN an_anagrafiche ON or_ordini.idanagrafica = an_anagrafiche.idanagrafica\r\nINNER JOIN an_sedi ON or_ordini.id_sede_partenza = an_sedi.id\r\nWHERE ((or_righe_ordini.qta - or_righe_ordini.qta_evasa) > 0) AND or_ordini.idstatoordine = 7 AND or_ordini.idtipoordine = 2\"} ]}',	'',	'',	'2022-12-21 14:51:31',	'2023-03-27 10:00:57');
